package JavaFiles;

public class program10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double circle_radius = 7.5;
		double cirle_perimeter = 2 * Math.PI * circle_radius;
        double circle_area = Math.PI * Math.pow(circle_radius, 2);

        System.out.println("Perimeter of a circle is = " + cirle_perimeter);
        System.out.println("Area of a circle is = " + circle_area);
	}

}
